text_test = """
>>><<><>><<<>><>>><<<>>><<<><<<>><>><<>>
"""

shapes = """
####

.#.
###
.#.

..#
..#
###

#
#
#
#

##
##
"""

def parse(text):

    res = []
    for line in text.split('\n'):
        if not line:
            continue

        for c in line:
            if c == '>':
                res.append(1)
            elif c == '<':
                res.append(0)

    return res


def parse_shapes(text):
    res = []
    shape = []
    for line in text.split('\n'):
        if not line:
            if shape:
                res.append(shape)
                shape = []
            continue

        shape.append(line)

    if shape:
        res.append(shape)

    return res

print(parse(text_test))


print(parse_shapes(shapes))


def next_elem(arr, iter):
    return (iter+1) % len(arr)


def valid_position(field, shape, x, y):
    for i, row in enumerate(shape):
        for j, c in enumerate(row):
            if c == '#':
                if x-i<0 or x-i>=len(field) or y+j<0 or y+j>=7 or field[x - i][y + j]:
                    return False
    return True


# полагаем, что вход валиден
def imprint(field, shape, x,y):
    for i, row in enumerate(shape):
        for j, c in enumerate(row):
            if c == '#':
                field[x-i][y+j] = True

def run(text):
    memo = dict()

    directions = parse(text)

    sh = parse_shapes(shapes)

    directions_iter = 0
    shape_iter = 0

    # 0 - это дно, фигуры падают вниз
    field = [[False] * 7 for _ in range(3000)]

    # первый свободный уровень - он же высота башни из камней
    highest_level = 0
    offset = 0

    # спавним фигуры по очереди, всего 2022
    shapenumber = -1
    goalnumber = 1000000000000
    while shapenumber+1 < goalnumber:
        shapenumber += 1

        if shapenumber % 1000000 == 0:
            print(shapenumber)
        shapeind = shapenumber % len(sh)
        shape = sh[shapeind]

        # на какой координате должна заспавниться фигура
        x,y = highest_level + 2 + len(shape), 2

        # print(x,y)

        # применяем сдувание + падение, пока фигура не остановится
        stopped = False
        while not stopped:
            # 0 - влево, 1 - вправо
            direction = directions[directions_iter]
            directions_iter = next_elem(directions, directions_iter)

            if direction == 0:
                try_y = y-1
            else:
                try_y = y+1

            if valid_position(field, shape, x, try_y):
                y = try_y

            # print(x, y)


            # пробуем упасть
            try_x = x - 1

            if valid_position(field, shape, try_x, y):
                x = try_x
            else:
                stopped = True
                # впечатываем фигуру в мапу
                imprint(field, shape, x, y)
                highest_level = max(highest_level, x+1)
                if highest_level > 2000:
                    offset += 1000
                    highest_level -= 1000
                    field = field[1000:]
                    for i in range(1000):
                        field.append([False]*7)

                # посмотреть, была ли раньше такая конфигурация параметров, экстраполировать до триллиона.
                # можно смотреть: номер фигуры, номер ветра, последние 20 рядов
                key = (shapeind, directions_iter, tuple(tuple(row) for row in field[highest_level-20:highest_level]))
                if key in memo:
                    # успех!
                    prev_level, prev_shapenumber = memo[key]
                    print(highest_level+offset, prev_level, key)

                    diff_heights = highest_level+offset - prev_level
                    diff_shapenumber = shapenumber - prev_shapenumber

                    mult = (goalnumber - shapenumber) // diff_shapenumber

                    shapenumber = shapenumber + mult * diff_shapenumber
                    offset += mult * diff_heights

                    # return
                memo[key] = (highest_level+offset, shapenumber)



    # for row in field[:highest_level][::-1]:
    #     print(''.join('.#'[int(i)] for i in row))

    print(highest_level+offset)


run(text)